mapper.select:
select userId
from user_token
where token = #{0}

mapper.update:
update user_token
set date    = #{date},
    version = #{version},
    ip      = #{ip}
where token = #{token}

mapper.delete:
delete
from user_token
where userId = #{param1}
  and clientFlag = #{param2}

delete
from user_token
where userId = #{param1}
  and deviceId = #{param2}
